        }
        return s;
    }

    function updateName (y, str, c) {
        return '\x1b[' + y + 'A'
            + '\x1b[1G'
            + '\x1b[1m\x1b[' + c + 'm'
            + trim(str)
            + '\x1b[0m'
            + '\x1b[' + y + 'B\x1b[1G'
